import React from 'react';

const ConsolidationTable = ({ acciones }) => {
  // Consolidate transactions for the same stock
  const consolidatedData = acciones.reduce((acc, accion) => {
    if (!acc[accion.nombre]) {
      acc[accion.nombre] = {
        cantidad: 0,
        valorTotal: 0
      };
    }
    acc[accion.nombre].cantidad += accion.numeroAcciones;
    acc[accion.nombre].valorTotal += accion.numeroAcciones * accion.valor;
    return acc;
  }, {});

  // Calculate final metrics
  const consolidatedStocks = Object.entries(consolidatedData).map(([nombre, data]) => {
    const precioPromedio = data.valorTotal / data.cantidad;
    // Assuming the latest price is available in the first matching action
    const precioActual = acciones.find(a => a.nombre === nombre)?.precioActual || 0;
    const gananciaPercentage = ((precioActual - precioPromedio) / precioPromedio) * 100;
    const gananciaUSD = (precioActual - precioPromedio) * data.cantidad;

    return {
      nombre,
      cantidadTotal: data.cantidad,
      valorUSD: data.valorTotal,
      precioCosto: precioPromedio,
      gananciaPercentage,
      gananciaUSD
    };
  });

  return (
    <div className="mt-8 p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-xl font-bold mb-4">Consolidación de Acciones</h2>
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="bg-gray-50">
              <th className="px-4 py-2 text-left">Acción</th>
              <th className="px-4 py-2 text-right">Cantidad Total</th>
              <th className="px-4 py-2 text-right">Valor USD</th>
              <th className="px-4 py-2 text-right">Precio de Costo</th>
              <th className="px-4 py-2 text-right">% Ganancia/Pérdida</th>
              <th className="px-4 py-2 text-right">$ Ganancia/Pérdida</th>
            </tr>
          </thead>
          <tbody>
            {consolidatedStocks.map((stock) => (
              <tr key={stock.nombre} className="border-b hover:bg-gray-50">
                <td className="px-4 py-2">{stock.nombre}</td>
                <td className="px-4 py-2 text-right">{stock.cantidadTotal}</td>
                <td className="px-4 py-2 text-right">${stock.valorUSD.toFixed(2)}</td>
                <td className="px-4 py-2 text-right">${stock.precioCosto.toFixed(2)}</td>
                <td className={`px-4 py-2 text-right ${stock.gananciaPercentage >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {stock.gananciaPercentage.toFixed(2)}%
                </td>
                <td className={`px-4 py-2 text-right ${stock.gananciaUSD >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                  ${Math.abs(stock.gananciaUSD).toFixed(2)}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ConsolidationTable;
