import React, { useEffect, useState } from 'react';
import { obtenerAcciones } from '../api';

const AccionesRegistradas = () => {
  const [acciones, setAcciones] = useState([]);

  useEffect(() => {
    const fetchAcciones = async () => {
      try {
        const data = await obtenerAcciones();
        setAcciones(data);
      } catch (error) {
        console.error('Error al obtener las acciones:', error.message);
      }
    };

    fetchAcciones();
  }, []);

  return (
    <div className="bg-white shadow-md rounded-lg p-6 mt-6">
      <h2 className="text-2xl font-bold mb-4">Acciones Registradas</h2>
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-100">
          <tr>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nombre</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Número de Acciones</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Valor Inicial</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Precio Actual</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Ganancia/Pérdida</th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {acciones.map((accion) => (
            <tr key={accion._id} className="hover:bg-gray-50">
              <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{accion.nombre}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{accion.numeroAcciones}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">${accion.valor}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">${accion.precioActual}</td>
              <td className={`px-6 py-4 whitespace-nowrap text-sm ${accion.ganancia >= 0 ? 'text-green-600' : 'text-red-600'}`}>{accion.ganancia}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AccionesRegistradas;
