import React, { useEffect, useState } from 'react';
import { obtenerPopularidadAcciones } from '../api';

const BarraPopularidad = () => {
  const [accionesPopulares, setAccionesPopulares] = useState([]);

  useEffect(() => {
    const fetchPopularidad = async () => {
      try {
        const popularidad = await obtenerPopularidadAcciones();
        setAccionesPopulares(popularidad);
      } catch (error) {
        console.error('Error al obtener la popularidad de las acciones:', error);
      }
    };
    
    fetchPopularidad();
  }, []);

  return (
    <div className="bg-white p-4 rounded-lg shadow-md mb-4 flex space-x-4 overflow-auto">
      {accionesPopulares.map((accion) => (
        <div key={accion.symbol} className={`flex items-center space-x-1 ${accion.change >= 0 ? 'text-green-600' : 'text-red-600'}`}>
          <span className="font-bold">{accion.symbol}</span>
          <span>{accion.change >= 0 ? '▲' : '▼'}</span>
          <span>{accion.change.toFixed(2)}%</span>
        </div>
      ))}
    </div>
  );
};

export default BarraPopularidad;
