import React, { useState, useEffect } from 'react';
import { PlusCircle } from 'lucide-react';
import RegistrarAccion from './registrarAccion';
import AccionesRegistradas from './accionesRegistradas';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { obtenerHistoricoAccion } from '../api';

const Dashboard = () => {
  const [accionesActualizadas, setAccionesActualizadas] = useState(false);
  const [accionSeleccionada, setAccionSeleccionada] = useState(null);
  const [historico, setHistorico] = useState([]);

  useEffect(() => {
    if (accionSeleccionada) {
      handleSeleccionarAccion(accionSeleccionada);
    }
  }, [accionesActualizadas]);

  const actualizarAcciones = () => {
    setAccionesActualizadas(!accionesActualizadas); // Para forzar la actualización de las acciones registradas
  };

  const handleSeleccionarAccion = async (accion) => {
    setAccionSeleccionada(accion);
    try {
      const historicoData = await obtenerHistoricoAccion(accion.nombre);
      setHistorico(historicoData);
    } catch (error) {
      console.error('Error al obtener el historial de la acción:', error.message);
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen p-8">
      <div className="max-w-7xl mx-auto grid grid-cols-12 gap-4">
        {/* Columna de Acciones Registradas */}
        <div className="col-span-4 bg-white rounded-lg shadow-md p-6">
          <div className="flex justify-between items-center mb-4">
            <h1 className="text-2xl font-bold text-gray-800">Acciones Registradas</h1>
            <button
              onClick={actualizarAcciones}
              className="flex items-center bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
            >
              <PlusCircle className="mr-2" /> Actualizar
            </button>
          </div>
          <AccionesRegistradas accionesActualizadas={accionesActualizadas} onSeleccionarAccion={handleSeleccionarAccion} />
        </div>

        {/* Columna de Detalles de la Acción */}
        <div className="col-span-8">
          <RegistrarAccion onAccionRegistrada={actualizarAcciones} />
          {accionSeleccionada && (
            <div className="mt-4 bg-white rounded-lg shadow-md p-6">
              <h2 className="text-2xl font-bold mb-4">
                {accionSeleccionada.nombre} ({accionSeleccionada.nombre})
              </h2>
              
              {/* Detalles de la Acción */}
              <div className="grid grid-cols-2 gap-4 mb-4">
                <div>
                  <h3 className="text-lg font-semibold">Detalles del Precio</h3>
                  <p><strong>Precio Actual:</strong> ${accionSeleccionada.precioActual}</p>
                  <p><strong>Precio de Apertura:</strong> ${accionSeleccionada.valor}</p>
                  <p><strong>Volumen:</strong> {accionSeleccionada.numeroAcciones}</p>
                </div>
                <div>
                  <h3 className="text-lg font-semibold">Análisis de Rendimiento</h3>
                  <p><strong>Clasificación:</strong> Mantener</p>
                  <p><strong>Proyección 12 meses:</strong> $75.00</p>
                </div>
              </div>

              {/* Gráfico de Rendimiento */}
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={historico}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="fecha" />
                  <YAxis />
                  <Tooltip />
                  <Line type="monotone" dataKey="precio" stroke="#8884d8" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
