import React, { useEffect, useState } from 'react';
import { obtenerAcciones } from '../api';
import { Bar } from 'react-chartjs-2';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

const AccionesRegistradas = () => {
  const classes = useStyles();
  const [acciones, setAcciones] = useState([]);

  useEffect(() => {
    const fetchAcciones = async () => {
      try {
        const data = await obtenerAcciones();
        setAcciones(data);
      } catch (error) {
        console.error('Error al obtener las acciones:', error.message);
      }
    };
    fetchAcciones();
  }, []);

  const data = {
    labels: acciones.map(accion => accion.nombre),
    datasets: [
      {
        label: 'Ganancia/Pérdida',
        data: acciones.map(accion => accion.ganancia),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  return (
    <div>
      <h2>Acciones Registradas</h2>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Nombre</TableCell>
              <TableCell align="right">Número de Acciones</TableCell>
              <TableCell align="right">Valor Inicial</TableCell>
              <TableCell align="right">Precio Actual</TableCell>
              <TableCell align="right">Ganancia/Pérdida</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {acciones.map((accion) => (
              <TableRow key={accion._id}>
                <TableCell component="th" scope="row">
                  {accion.nombre}
                </TableCell>
                <TableCell align="right">{accion.numeroAcciones}</TableCell>
                <TableCell align="right">{accion.valor}</TableCell>
                <TableCell align="right">{accion.precioActual}</TableCell>
                <TableCell align="right">{accion.ganancia}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <div style={{ marginTop: '2rem' }}>
        <h3>Gráfico de Ganancia/Pérdida</h3>
        <Bar data={data} />
      </div>
    </div>
  );
};

export default AccionesRegistradas;
