import React, { useEffect, useState } from 'react';
import { obtenerAcciones } from '../api';

const AccionesRegistradas = () => {
  const [acciones, setAcciones] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAcciones = async () => {
      try {
        const data = await obtenerAcciones();
        setAcciones(data);
      } catch (error) {
        console.error('Error al obtener las acciones:', error.message);
        setError('No se pudieron cargar las acciones');
      }
    };

    fetchAcciones();
  }, []);

  // Helper function to determine color based on gain/loss
  const getColorClass = (valor) => {
    return valor >= 0 
      ? 'text-green-600 bg-green-50' 
      : 'text-red-600 bg-red-50';
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4 text-center">Acciones Registradas</h2>
      
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="overflow-x-auto shadow-md rounded-lg">
        <table className="w-full table-auto">
          <thead className="bg-gray-100">
            <tr>
              {['Nombre', 'Número de Acciones', 'Valor Inicial', 'Precio Actual', 'Ganancia/Pérdida'].map((header) => (
                <th key={header} className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {acciones.map((accion) => (
              <tr key={accion._id} className="hover:bg-gray-50 transition-colors">
                <td className="px-4 py-3 whitespace-nowrap">{accion.nombre}</td>
                <td className="px-4 py-3 whitespace-nowrap text-right">{accion.numeroAcciones}</td>
                <td className="px-4 py-3 whitespace-nowrap text-right">
                  ${accion.valor.toFixed(2)}
                </td>
                <td className="px-4 py-3 whitespace-nowrap text-right">
                  ${accion.precioActual.toFixed(2)}
                </td>
                <td className={`px-4 py-3 whitespace-nowrap text-right font-semibold ${getColorClass(accion.ganancia)}`}>
                  ${accion.ganancia.toFixed(2)}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        
        {acciones.length === 0 && !error && (
          <div className="text-center py-4 text-gray-500">
            No hay acciones registradas
          </div>
        )}
      </div>

      {/* Summary Statistics */}
      {acciones.length > 0 && (
        <div className="mt-4 grid grid-cols-2 gap-4">
          <div className="bg-blue-50 p-3 rounded-lg">
            <h3 className="text-blue-600 font-bold">Total Inversión</h3>
            <p className="text-xl">
              ${acciones.reduce((total, accion) => total + (accion.valor * accion.numeroAcciones), 0).toFixed(2)}
            </p>
          </div>
          <div className={`p-3 rounded-lg ${
            acciones.reduce((total, accion) => total + accion.ganancia, 0) >= 0 
              ? 'bg-green-50 text-green-600' 
              : 'bg-red-50 text-red-600'
          }`}>
            <h3 className="font-bold">Ganancia Total</h3>
            <p className="text-xl">
              ${acciones.reduce((total, accion) => total + accion.ganancia, 0).toFixed(2)}
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default AccionesRegistradas;
