import React, { useState } from 'react';
import { registrarAccion } from '../api';

const RegistrarAccion = ({ onAccionRegistrada }) => {
  const [nombre, setNombre] = useState('');
  const [numeroAcciones, setNumeroAcciones] = useState('');
  const [valor, setValor] = useState('');
  const [fecha, setFecha] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const accionData = { nombre, numeroAcciones, valor, fecha };
    
    try {
      const accionRegistrada = await registrarAccion(accionData);
      onAccionRegistrada(accionRegistrada); // Llamar la función para actualizar la lista de acciones
      setNombre('');
      setNumeroAcciones('');
      setValor('');
      setFecha('');
    } catch (error) {
      alert('Error al registrar la acción');
    }
  };

  return (
    <div>
      <h2>Registrar Acción</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Símbolo:</label>
          <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
        </div>
        <div>
          <label>Número de Acciones:</label>
          <input type="number" value={numeroAcciones} onChange={(e) => setNumeroAcciones(e.target.value)} required />
        </div>
        <div>
          <label>Valor:</label>
          <input type="number" value={valor} onChange={(e) => setValor(e.target.value)} required />
        </div>
        <div>
          <label>Fecha:</label>
          <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} required />
        </div>
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
};

export default RegistrarAccion;
