import React from 'react';

const ConsolidationTable = ({ acciones }) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = date.getDate();
    const month = date.toLocaleString('es', { month: 'short' }).toLowerCase();
    return `${day}-${month}`;
  };

  // Calculate running totals and cost price for each row
  const calculateRunningTotals = () => {
    let totalUSD = 0;
    let totalQuantity = 0;
    
    return acciones.map(accion => {
      totalUSD += accion.valor / accion.numeroAcciones;
      totalQuantity += accion.numeroAcciones;
      
      return {
        ...accion,
        runningTotalUSD: totalUSD,
        runningTotalQuantity: totalQuantity,
        costPrice: totalUSD / totalQuantity
      };
    });
  };

  const accionesWithTotals = calculateRunningTotals();

  return (
    <div className="mt-8 p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-xl font-bold mb-4">Registro de Transacciones</h2>
      <div className="overflow-x-auto">
        <table className="w-full border-collapse">
          <thead>
            <tr className="bg-gray-50">
              <th className="border p-2 text-left">FECHA COMPRA</th>
              <th className="border p-2 text-left">ACCIÃ“N</th>
              <th className="border p-2 text-right">PRECIO COMPRA</th>
              <th className="border p-2 text-right">CANTIDAD COMPRADA</th>
              <th className="border p-2 text-right">USD COMPRA</th>
              <th className="border p-2 text-right">PRECIO DE COSTO</th>
            </tr>
          </thead>
          <tbody>
            {accionesWithTotals.map((accion, index) => {
              const usdValue = accion.valor * accion.numeroAcciones;
              return (
                <tr key={index} className="hover:bg-gray-50">
                  <td className="border p-2">{formatDate(accion.fecha)}</td>
                  <td className="border p-2">{accion.nombre}</td>
                  <td className="border p-2 text-right">{accion.valor.toFixed(2)}</td>
                  <td className="border p-2 text-right">{accion.numeroAcciones}</td>
                  <td className="border p-2 text-right">{usdValue.toFixed(2)}</td>
                  <td className="border p-2 text-right">{accion.costPrice.toFixed(2)}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};



export default ConsolidationTable;
